def run_app():
    API_KEY = "Nw7B5zIVbneyHxOR3akRxrm293diEpPAg38BANdIvWaGwLJd2sUAyh68HRc0nlHY"
    address = "0x6F3864963eA910a41b262746995cF64603d5f72C"
    chain = "avalanche"
    cursor = ""
    do = True
    try: 
        while do:
            params = {
                "address": address,
                "chain": chain,
                "limit": 100,
                "cursor": cursor,
                "format": "decimal", 
                "normalizeMetadata": True,
            }

            response = evm_api.nft.get_nft_owners(
            api_key=API_KEY,
            params=params,)
            print(
                f"Got page {response['page']}")

            for owner in response["result"]:
                owners[owner["owner_of"]] = {
                    "owner": owner["owner_of"],
                    "tokenId": owner["token_id"],
                }
            
            cursor = response["cursor"]
            if cursor == "" and cursor is None:
                do = False
    except Exception as e:
        print(e)
    finally:
        print("total owners:", len(owners))
        contract = web3.eth.contract(address=address, abi=abi)

        for key in owners:
          print("owaddress", key)
          nftbalance = contract.functions.balanceOf(Web3.toChecksumAddress(key)).call()
          print("NFT", nftbalance)
          owners[key] = {
            "amount": nftbalance
          }

        with open("./NFTHolderInfoForSleeFiJewel.json", "w") as f:
            f.write(json.dumps(owners))

run_app()
